import { useState } from "react";

export default function Webflow() {
  const slides = [
    {
      text: `"Security Council transformed our cybersecurity posture, providing invaluable insights and support that exceeded our expectations."`,
      name: "John Doe",
      role: "CISO, TechCorp",
      img: "./pic.png",
    },
    {
      text: `"Amazing service, our company is now more secure than ever!"`,
      name: "Jane Smith",
      role: "CTO, FinSecure",
      img: "./pic.png",
    },
    {
      text: `"They provided outstanding guidance for our security journey."`,
      name: "Michael Lee",
      role: "CEO, SafeNet",
      img: "./pic.png",
    },
  ];

  const [current, setCurrent] = useState(0);

  const nextSlide = () => {
    setCurrent((prev) => (prev + 1) % slides.length);
  };

  return (
    <div className="w-full h-[503px] flex justify-center items-center flex-col relative">
      <div className="w-[60%] h-[65%] transition-all duration-500">
        {/* Arrow */}
        <img
          src="./arrow.png"
          className="w-[48px] h-[48px] absolute right-28 top-60 cursor-pointer"
          onClick={nextSlide}
        />

        {/* Logo */}
        <div className="w-full h-[25%] flex justify-center">
          <img src="./webflow.png" className="w-[120px] h-[48px]" />
        </div>

        {/* Text */}
        <div className="w-full h-[30%] font-bold text-2xl text-center">
          {slides[current].text}
        </div>

        {/* Profile */}
        <div className="w-full h-[45%] flex justify-center items-center">
          <div className="w-[25%] h-full flex justify-center items-center flex-col">
            <img src={slides[current].img} className="w-[64px] h-[64px]" />
            <div className="text-base font-semibold">{slides[current].name}</div>
            <div>{slides[current].role}</div>
          </div>
        </div>
      </div>

      {/* Dots */}
      <div className="w-[60%] h-[15%] flex justify-center items-center gap-x-1">
        {slides.map((_, index) => (
          <div
            key={index}
            onClick={() => setCurrent(index)}
            className={`w-5 h-5 rounded-full cursor-pointer ${
              index === current ? "bg-black" : "bg-black/20"
            }`}
          ></div>
        ))}
      </div>
    </div>
  );
}
