import { useState } from "react";

export default function Webflow() {
  const slides = [
    {
      text: `"Security Council transformed our cybersecurity posture, providing invaluable insights and support that exceeded our expectations."`,
      name: "John Doe",
      role: "CISO, TechCorp",
      img: "./pic.png",
    },
    {
      text: `"Amazing service, our company is now more secure than ever!"`,
      name: "Jane Smith",
      role: "CTO, FinSecure",
      img: "./pic.png",
    },
  ];

  const [current, setCurrent] = useState(0);

  return (
    <div className="w-full h-[503px] flex justify-center items-center flex-col relative overflow-hidden">
      <div className="w-[60%] h-[65%] relative">
        {/* Arrow */}
        <img
          src="./arrow.png"
          className="w-[48px] h-[48px] absolute right-[-70px] top-40 cursor-pointer z-10"
          onClick={() => setCurrent((current + 1) % slides.length)}
        />

        {/* Slides wrapper */}
        <div
          className="flex w-full h-full transition-transform duration-700 ease-in-out"
          style={{ transform: `translateX(-${current * 100}%)` }}
        >
          {slides.map((s, i) => (
            <div key={i} className="w-full flex-shrink-0 flex flex-col items-center justify-center px-6">
              {/* Logo */}
              <div className="w-full h-[25%] flex justify-center">
                <img src="./webflow.png" className="w-[120px] h-[48px]" />
              </div>

              {/* Text */}
              <div className="w-full h-[30%] font-bold text-2xl text-center">
                {s.text}
              </div>

              {/* Profile */}
              <div className="w-full h-[45%] flex justify-center items-center">
                <div className="w-[25%] h-full flex justify-center items-center flex-col">
                  <img src={s.img} className="w-[64px] h-[64px]" />
                  <div className="text-base font-semibold">{s.name}</div>
                  <div>{s.role}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Dots */}
      <div className="w-[60%] h-[15%] flex justify-center items-center gap-x-1 mt-2">
        {slides.map((_, index) => (
          <div
            key={index}
            onClick={() => setCurrent(index)}
            className={`w-5 h-5 rounded-full cursor-pointer transition-colors duration-300 ${
              index === current ? "bg-black" : "bg-black/20"
            }`}
          ></div>
        ))}
      </div>
    </div>
  );
}
